import * as actionTypes from './actionTypes';
import axios from 'axios'

export const authStart = () => {
    return {
        type:actionTypes.AUTH_START
    }
}

export const authSuccess = (token,userId) => {
    return {
        type:actionTypes.AUTH_SUCCESS,
        idToken:token,
        userId:userId
    }
}

export const authFail = (error) => {
    return {
        type:actionTypes.AUTH_FAIL,
        error:error
    }
}

export const logout = () => {
    return {
        type:actionTypes.AUTH_LOGOUT
    }
}

export const checkAuthTimeout = (expirationTime) => {
    return dispatch => {
        setTimeout(() => {
            dispatch(logout())
        }, expirationTime *1000);
    }
}

export const auth = (email,password,isSignUp) => {
    return dispatch => {
        dispatch(authStart());
        const authData = {
            email:email,
            password:password,
            returnSecureToken: true
        }
        let url = "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB4JzDs9KnhzyHX2e0vzuk9aZujYwsmCiw"
        if(!isSignUp){
            url = "https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyB4JzDs9KnhzyHX2e0vzuk9aZujYwsmCiw"
        }
        axios.post(url,authData)
            .then(resp=>{
                console.log(resp)
                const expirationDate = new Date(new Date().getTime + resp.data.expiresIn * 1000)
                localStorage.setItem("token",resp.data.idToken)
                localStorage.setItem("expirationDate",resp.data.idToken)
                dispatch(authSuccess(resp.data.idToken,resp.data.localId))
                dispatch(checkAuthTimeout(resp.data.expiresIn))
            })
            .catch(err=>{
                console.log(err)
                dispatch(authFail(err.response.data.error))
            })
    }
}

export const setAuthRedirectPath = (path) => {
    return {
        type:actionTypes.SET_AUTH_REDIRECT_PATH,
        path:path
    }
}
