import React , {Component} from 'react'
import Button from '../../../components/UI/Button/Button'
import classes from './ContactData.module.css'
import Spinner from '../../../components/UI/Spinner/Spinner'
import Input from '../../../components/UI/Input/Input'
import axios from '../../../axios-orders'

class ContactData extends Component {
    state = {
        orderForm:{
            name:{
                elementType:'input',
                elementConfig:{
                    type:'text',
                    placeholder:'Your name'
                },
                value:'',
                validation:{
                    required: true
                },
                valid:true
            },
            street:{
                elementType:'input',
                elementConfig:{
                    type:'text',
                    placeholder:'Street'
                },
                value:'',
                validation:{
                    required: true
                },
                valid:true
            },
            zip:{
                elementType:'input',
                elementConfig:{
                    type:'text',
                    placeholder:'ZIP Code'
                },
                value:'',
                validation:{
                    required: true
                },
                valid:true
            },
            country:{
                elementType:'input',
                elementConfig:{
                    type:'text',
                    placeholder:'Country'
                },
                value:'',
                validation:{
                    required: true
                },
                valid:true
            },
            email:{
                elementType:'input',
                elementConfig:{
                    type:'email',
                    placeholder:'Your mail'
                },
                value:'',
                validation:{
                    required: true
                },
                valid:true
            },
            deliveryMethod: {
                elementType:'select',
                elementConfig:{
                    options:[
                        {value:'fastest',displayValue:'Fastest'},
                        {value:'cheapest',displayValue:'Cheapest'}
                    ]
                },
                value:'',
            },
        },
        loading:false

    }


    chechValidity(value,rules){
        let isValid = false;
        if(rules.required){
            isValid = value.trim() !== '';
        }

        return isValid;
    }

    orderHandler = (event) =>{
        event.preventDefault()
    this.setState({loading:true})
    const formData = {}
    for(let orderDataIdentifier in this.state.orderForm){
        formData[orderDataIdentifier] = this.state.orderForm[orderDataIdentifier].value
    }
    const order = {
      ingredients: this.props.ingredients,
      price: this.props.totalPrice,
      orderData:formData
     
    }
    axios.post('/orders.json',order)
        .then(resp=>{
            this.setState({loading:false })
            this.props.history.push('/')
        })
        .catch(error=>this.setState({loading:false}))

    }

    inputChangedHandler = (event,inputIdentifier) => {
        const updatedOrderForm = {
            ...this.state.orderForm
        }
        const updatedFormElement = {
            ...updatedOrderForm[inputIdentifier]
        }
        updatedFormElement.value = event.target.value;
        updatedFormElement.valid = this.chechValidity(updatedFormElement.value,updatedFormElement.validation)
        updatedOrderForm[inputIdentifier] = updatedFormElement;
        this.setState({orderForm:updatedOrderForm})

    }

    render(){
        const formElementsArray = [];
        for(let key in this.state.orderForm){
            formElementsArray.push({
                id:key,
                config:this.state.orderForm[key]
            })

        }

        let form = (<form onSubmit={this.orderHandler}>
            {formElementsArray.map(formElement=>(
                <Input key={formElement.id}
                       elementType={formElement.config.elementType}
                       elementConfig={formElement.config.elementConfig}
                       value={formElement.config.value}
                       changed={(event)=>this.inputChangedHandler(event,formElement.id)} />
            ))}
            <Button btnType = 'Success' >Order</Button>
        </form>);

        if(this.state.loading){
           form = <Spinner />
        }
        return (
            <div className={classes.ContactData}>
                <h1>Enter your contact data </h1>
                {form}
            </div>
        )

    }
}

export default ContactData
